const OUTPUT_COUNT = 8;
const LED_ON = true;
const LED_OFF = false;
const LAST_OUTPUT = OUTPUT_COUNT - 1;

let lastOutputs = context.get("last_out")||null;
if (lastOutputs === null) {
  // no array yet: create and populate so "next" step will start at the beginning
  lastOutputs = Array(OUTPUT_COUNT);
  lastOutputs.fill(null);
  lastOutputs[0] = LED_OFF;
}
// initialize last state for "backward", so "next" will reverse to first forward state
let stepForward = context.get("step_forward")||false;
let reversed = false;

// Handle the end cases, where direction changes, but not the message target
if (!stepForward && lastOutputs[0] !== null) {
  stepForward = true;
  lastOutputs[0] = LED_ON;
  reversed = true;
} else if (stepForward && lastOutputs[LAST_OUTPUT] !== null) {
  stepForward = false;
  lastOutputs[LAST_OUTPUT] = LED_OFF;
  reversed = true;
}

if (reversed) {
  // Reverse direction without changing target of message
  context.set("step_forward", stepForward);
} else if (stepForward) {
  // Change message target one step forward
  lastOutputs.pop();
  lastOutputs.unshift(null);
} else {
  // Change message target one step backward
  lastOutputs.shift();
  lastOutputs.push(null);
}
context.set("last_out", lastOutputs);

// [Re]generate each output message each time, because they are passed by reference,
// and the message consumer could modify them inappropriately for the current context
let msgSet = Array(OUTPUT_COUNT);
msgSet.fill(null);
for (let i = 0; i < OUTPUT_COUNT; i++) {
  if (lastOutputs[i] !== null) {
    msgSet[i] = { "payload": lastOutputs[i], "topic": msg.topic, "_msgid": msg._msgid };
  }
}

return msgSet;
